import React from "react";
import { useState } from "react";
import "./signIn.css";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { toastError, toastSuccess } from "../../../helpers/toast";
function SignUp() {
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [wrongID, setwrongID] = useState(false);
  const navigate = useNavigate();
  const [notificationBar, setnotificationBar] = useState(false);
  const [verifyEmail, setverifyEmail] = useState(false);
  const [otp, setOtp] = useState();
  const [inputOtp, setInputOtp] = useState();
  const [wrongOtp, setwrongOtp] = useState(false);
  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const [signUpInfo, setsignUpInfo] = useState({
    username: "",
    password: "",
    first_name: "",
    last_name: "",
    email: "",
    phone_number: "",
    role: "User",
  });

  const navigateToSignIn = () => {
    setnotificationBar(true);

    setTimeout(() => {
      setnotificationBar(false);
      // Navigate to another page after the delay
      navigate("/sign-in");
    }, 3000); // 3 seconds delay
    // Pop up the purchase completed
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      console.log(signUpInfo);
      const response = await axios.post(
        "http://127.0.0.1:8000/auth/register",
        signUpInfo
      );
      console.log(response.data);
      try {
        const response = await axios.get(
          `http://127.0.0.1:8000/auth/otp/${signUpInfo.email}`
        );
        setOtp(response.data.otp);
        setverifyEmail(true);
      } catch (error) {}
    } catch (error) {
      console.error(error);
      console.log(signUpInfo);
      setwrongID(true);
    } finally {
      setLoading(false);
    }
  };

  const handleCancelOTP = () => {
    setverifyEmail(false)
  };

  const handleOTP = async (e) => {
    if (otp == inputOtp) {
      setverifyEmail(false);
      try {
        const response = await axios.post(
          "http://127.0.0.1:8000/auth/register-accept",
          signUpInfo
        );
        console.log(response.data);
        toastSuccess("Login succeeded!");
      } catch (error) {
        console.log(signUpInfo);
        toastError(error);
      }
      navigateToSignIn();
    } else {
      setwrongOtp(true);
    }
  };

  const handleChange = ({ currentTarget: input }) => {
    setsignUpInfo({ ...signUpInfo, [input.name]: input.value });
  };

  return (
    <div>
      <div className="login-container">
        <h2>Sign Up</h2>
        <form onSubmit={handleSubmit}>
          {wrongID && (
            <div className="wrong-id">
              <i> * Username already exists! </i>
            </div>
          )}
          <div className="input-group">
            <input
              type="text"
              id="username"
              onChange={handleChange}
              value={signUpInfo.username}
              name="username"
              placeholder="Username"
              required
            />
          </div>
          <div className="input-group">
            <input
              type={showPassword ? "text" : "password"}
              id="password"
              name="password"
              onChange={handleChange}
              value={signUpInfo.password}
              placeholder="Password"
              required
            />
            <span
              className="toggle-password"
              onClick={togglePasswordVisibility}
            >
              üëÅ
            </span>
          </div>
          <div className="input-group">
            <input
              type="text"
              id="first_name"
              name="first_name"
              onChange={handleChange}
              value={signUpInfo.first_name}
              placeholder="First name"
              required
            />
          </div>
          <div className="input-group">
            <input
              type="text"
              id="last_name"
              name="last_name"
              onChange={handleChange}
              value={signUpInfo.last_name}
              placeholder="Last name"
              required
            />
          </div>
          <div className="input-group">
            <input
              type="email"
              id="email"
              name="email"
              onChange={handleChange}
              value={signUpInfo.email}
              placeholder="Email address"
              required
            />
          </div>

          {loading ? (
            <div className="sign_in_loading">
              <i class="fa-solid fa-spinner fa-spin"></i>
            </div>
          ) : (
            <button type="submit" className="login-button">
              Sign Up
            </button>
          )}
        </form>
        <a href="#" className="forgot-password">
          Forgot your password?
        </a>
        <p className="switch-form">
          Already have an account? <Link to="/sign-in">Sign In</Link>
        </p>
      </div>

      {verifyEmail && (
        <div className="sign-up-confirming-surround">
          {" "}
          <div className="sign-up-confirming-box">
            <h3>
              Please enter the OTP sent to email address: {signUpInfo.email}{" "}
            </h3>
            {wrongOtp && <i>*Wrong OTP</i>}

            <input className="ad-otp-input-box"
              type="number"
              value={inputOtp}
              onChange={(e) => setInputOtp(e.target.value)}
            ></input>
            <div className="ad-otp-buttons">
            <button className="ad-otp-submit-button" onClick={handleOTP}>Submit</button>
            <button className="ad-otp-cancel-button" onClick={handleCancelOTP}>Cancel</button>

            </div>

          </div>
        </div>
      )}

      {notificationBar && (
        <div className="sign-up-confirming-surround">
          {" "}
          <div className="sign-up-confirming-box">
            <i class="fa-solid fa-spinner fa-spin"></i>
            <h3>Sign Up successfully. Navigating to Sign In page </h3>{" "}
          </div>
        </div>
      )}
    </div>
  );
}

export default SignUp;
