import React from 'react';
import './Overview.css'

const Overview = () => {
    return (
        <div className='content-overview'>
            <p>This 2-week beginner's course introduces students to the basics of the Japanese language, focusing on essential grammar, vocabulary, pronunciation, and cultural context.</p>
            <p>By the end of this course, students will be able to engage in simple conversations, read and write basic hiragana and katakana, and understand simple sentence structures.</p>
        </div>
    );
};

export default Overview;
